    async def build_hacktivity_search_query(query_string="disclosed:true", size=100, offset=0, sort_field="disclosed_at", sort_direction="ASC"):
        query = {
            "operationName": "HacktivitySearchQuery",
            "variables": {
                "queryString": query_string,
                "size": size,
                "from": offset,
                "sort": {"field": sort_field, "direction": sort_direction},
                "product_area": "hacktivity",
                "product_feature": "overview",
            },
            "query": "query HacktivitySearchQuery($queryString: String!, $from: Int, $size: Int, $sort: SortInput!) {\n  me {\n    id\n    __typename\n  }\n  search(\n    index: CompleteHacktivityReportIndex\n    query_string: $queryString\n    from: $from\n    size: $size\n    sort: $sort\n  ) {\n    __typename\n    total_count\n    nodes {\n      __typename\n      ... on HacktivityDocument {\n        id\n        _id\n        reporter {\n          id\n          username\n          name\n          __typename\n        }\n        cve_ids\n        cwe\n        severity_rating\n        upvoted: upvoted_by_current_user\n        public\n        report {\n          id\n          databaseId: _id\n          title\n          substate\n          url\n          disclosed_at\n          report_generated_content {\n            id\n            hacktivity_summary\n            __typename\n          }\n          __typename\n        }\n        votes\n        team {\n          id\n          handle\n          name\n          medium_profile_picture: profile_picture(size: medium)\n          url\n          currency\n          __typename\n        }\n        total_awarded_amount\n        latest_disclosable_action\n        latest_disclosable_activity_at\n        submitted_at\n        disclosed\n        has_collaboration\n        collaborators {\n          id\n          username\n          name\n          __typename\n        }\n        __typename\n      }\n    }\n  }\n}\n"
        }
        return query
    
