    async def build_profile_info_query(username="godiego"):
        query = {
            "operationName":"UserProfilePageQuery",
            "variables":{"resourceIdentifier":username,"product_area":"leaderboard","product_feature":"details"},
            "query":"query UserProfilePageQuery($resourceIdentifier: String!) {\n  me {\n    id\n    username\n    ...UserProfileMe\n    ...UserProfileCardMe\n    ...UserStatsMe\n    __typename\n  }\n  user(username: $resourceIdentifier) {\n    id\n    username\n    name\n    intro\n    profileActivated: profile_activated\n    pentester_profile {\n      id\n      ...PentestsPentesterProfile\n      __typename\n    }\n    user_streak {\n      id\n      length\n      start_date\n      end_date\n      __typename\n    }\n    ...UserProfileUser\n    ...UserProfileCardUser\n    ...CreditsUser\n    ...BadgesUser\n    ...ReviewUser\n    ...UserStatsUser\n    __typename\n  }\n  ...UserProfileCardIdV\n}\n\nfragment UserProfileMe on User {\n  id\n  username\n  __typename\n}\n\nfragment UserProfileUser on User {\n  id\n  username\n  __typename\n}\n\nfragment UserProfileCardUser on User {\n  id\n  created_at\n  location\n  website\n  bio\n  name\n  username\n  profile_picture(size: large)\n  bugcrowd_handle\n  hack_the_box_handle\n  github_handle\n  gitlab_handle\n  linkedin_handle\n  twitter_handle\n  cleared\n  verified\n  open_for_employment\n  mark_as_company_on_leaderboards\n  ...FollowUser\n  __typename\n}\n\nfragment FollowUser on User {\n  id\n  followed\n  __typename\n}\n\nfragment UserProfileCardMe on User {\n  id\n  __typename\n}\n\nfragment UserProfileCardIdV on Query {\n  id_verification {\n    status\n    __typename\n  }\n  __typename\n}\n\nfragment PentestsPentesterProfile on PentesterProfile {\n  id\n  completed_pentests_number\n  __typename\n}\n\nfragment CreditsUser on User {\n  id\n  username\n  resolved_report_count\n  thanks_items_total_count\n  __typename\n}\n\nfragment BadgesUser on User {\n  id\n  username\n  badges(first: 3) {\n    edges {\n      awarded_at\n      node {\n        id\n        name\n        image_path\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment ReviewUser on User {\n  id\n  public_reviews(first: 5) {\n    edges {\n      node {\n        id\n        public_feedback\n        team {\n          id\n          name\n          handle\n          __typename\n        }\n        __typename\n      }\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment UserStatsUser on User {\n  id\n  username\n  __typename\n}\n\nfragment UserStatsMe on User {\n  id\n  __typename\n}\n"
        }
        return query